import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../Context/AuthContext'; // Ensure hook name matches
import { Navigate, useNavigate } from 'react-router-dom';

const Useradd = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [repassword, setrePassword] = useState('');
  const [role, setrole] = useState('student');
  

  const [error, setError]  = useState('');
  const { authuser, setauthuser, setisloggedin , isloggedin } = useAuth(); // Ensure hook name matches
  const route = "http://localhost:5000";  
  const navigate = useNavigate()
  const handlechange =(event) =>{
    setrole()
  }
  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post(`${route}/api/adduser`, {email, password,role});
      const res = response.data;

    } catch (error) {
      console.error('Login error:', error);
      setError('Invalid credentials. Please try again.');
    }
  };

  return (
    <div className='logindivcard'>
      <form className='loginformdiv d-flex flex-column justify-content-center' onSubmit={handleSubmit}>
        <h1 className='loginheading'>Login</h1>
        <div className="mb-2">
          <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
          <input
            type="email"
            className="form-control"
            id="exampleInputEmail1"
            aria-describedby="emailHelp"
            value={email}
            onChange={(e) => { setEmail(e.target.value); setError(''); }}
          />
          <div id="emailHelp" className="form-text mb-3">We'll never share your email with anyone else.</div>
        </div>
        <div className="mb-4">
          <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
          <input
            type="password"
            className="form-control"
            id="exampleInputPassword1"
            value={password}
            onChange={(e) => { setPassword(e.target.value); setError(''); }}
          />
        </div>
        <div className="mb-4">
          <label htmlFor="exampleInputPassword1" className="form-label">Re-Enter Password</label>
          <input
            type="password"
            className="form-control"
            id="exampleInputPassword1"
            value={repassword}
            onChange={(e) => { setrePassword(e.target.value); setError(''); }}
          />
        </div>
        <div className="mb-3">
        <label htmlFor="selectOption" className="form-label">Select an Option</label>
        <select id="selectOption" className="form-select" onChange={(e) => { setrole(e.target.value)}}>
          <option value="student">Option 1</option>
          <option value="teacher" disabled={false}>Option 2</option>
        </select></div>
        {error && <p className="text-danger">{error}</p>}
        <button type="submit" className="btn btn-primary mt-4">Submit</button>
      </form>
    </div>
  );
};

export default Useradd;
