import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const route = "http://localhost:5000"  
  const handleSubmit = async (event) => {
    event.preventDefault(event);
    // Handle form submission, e.g., send data to server
    console.log('Form submitted', { email, password });
    try {
      const response = await axios.post(route+'/api/login', {
        email,
        password,
      });
      const { email , password, role } = response.data;
      console.log(response.data)
      localStorage.setItem('user', email); // Store token in local storage
      onLogin(role); // Pass user data to parent component (App.js)

    } catch (error) {
      console.error('Login error:', error);
      setError('Invalid credentials. Please try again.');
    }
  };

  return (
    <div className='logindivcard'>
      <form className='loginformdiv d-flex flex-column justify-content-center' onSubmit={handleSubmit}>
        <h1 className='mb-5'>Login</h1>
        <div className="mb-3">
          <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
          <input
            type="email"
            className="form-control"
            id="exampleInputEmail1"
            aria-describedby="emailHelp"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
        </div>
        <div className="mb-3">
          <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
          <input
            type="password"
            className="form-control"
            id="exampleInputPassword1"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
};

export default Login;
