import React, { useState } from 'react';
import axios from 'axios';

const CreateClassroom = () => {
    const [className, setClassName] = useState('');
    const [schedule, setSchedule] = useState({
        Monday: { start: '', end: '' },
        Tuesday: { start: '', end: '' },
        Wednesday: { start: '', end: '' },
        Thursday: { start: '', end: '' },
        Friday: { start: '', end: '' },
        Saturday: { start: '', end: '' },
        Sunday: { start: '', end: '' },
    });

    const handleInputChange = (day, field, value) => {
        setSchedule(prevSchedule => ({
            ...prevSchedule,
            [day]: {
                ...prevSchedule[day],
                [field]: value
            }
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        const classroomData = {
            className,
            schedule
        };

        try {
            await axios.post('http://localhost:5000/api/createclassroom', classroomData);
            alert('Classroom created successfully!');
        } catch (error) {
            console.error('Error creating classroom:', error);
        }
    };

    return (
        <div className="container mt-5">
            <h2>Create Classroom</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="className">Classroom Name</label>
                    <input 
                        type="text" 
                        className="form-control" 
                        id="className" 
                        value={className} 
                        onChange={(e) => setClassName(e.target.value)} 
                        required 
                    />
                </div>

                {Object.keys(schedule).map(day => (
                    <div key={day} className="form-group">
                        <h5>{day}</h5>
                        <div className="row">
                            <div className="col">
                                <label htmlFor={`${day}-start`}>Start Time</label>
                                <input 
                                    type="time" 
                                    className="form-control" 
                                    id={`${day}-start`} 
                                    value={schedule[day].start} 
                                    onChange={(e) => handleInputChange(day, 'start', e.target.value)} 
                                />
                            </div>
                            <div className="col">
                                <label htmlFor={`${day}-end`}>End Time</label>
                                <input 
                                    type="time" 
                                    className="form-control" 
                                    id={`${day}-end`} 
                                    value={schedule[day].end} 
                                    onChange={(e) => handleInputChange(day, 'end', e.target.value)} 
                                />
                            </div>
                        </div>
                    </div>
                ))}

                <button type="submit" className="btn btn-primary mt-3">Create Classroom</button>
            </form>
        </div>
    );
};

export default CreateClassroom;
